Level_GetBgm:
		tst.w	(Demo_mode_flag).w
		bmi.s	Level_LoadData
		moveq	#0,d0
		move.b	(Current_Zone).w,d0
		lea	MusicList(pc),a1
		move.b	(a1,d0.w),d0
		bsr.w	PlayMusic
		move.b	#$34,(TitleCard).w
