; ===========================================================================
; ---------------------------------------------------------------------------
; Subroutine to animate Sonic's sprites
; See also: AnimateSprite
; ---------------------------------------------------------------------------

; ||||||||||||||| S U B R O U T I N E |||||||||||||||||||||||||||||||||||||||

; loc_10AB2:
Sonic_Animate:
		lea	(SonicAniData).l,a1
		moveq	#0,d0
		move.b	$1C(a0),d0
		cmp.b	$1D(a0),d0	; has the animation changed?
		beq.s	SAnim_Do	; if not,branch
		move.b	d0,$1D(a0)	; set previous animation
		move.b	#0,$1B(a0)	; reset animation frame
		move.b	#0,$1E(a0)	; reset animation duration
		bclr	#5,$22(a0)	; clear 'pushing' flag
; loc_10ADA:
SAnim_Do:
		add.w	d0,d0
		adda.w	(a1,d0.w),a1	; calculate address of appropriate animation script
		move.b	(a1),d0
		bmi.s	SAnim_WalkRun	; if animation is walk/run/roll/jump,branch
		move.b	$22(a0),d1
		andi.b	#1,d1
		andi.b	#$FC,1(a0)
		or.b	d1,1(a0)
		subq.b	#1,$1E(a0)	; subtract 1 from frame duration
		bpl.s	SAnim_Delay	; if time remains,branch
		move.b	d0,$1E(a0)	; load frame duration
; loc_10B00:
SAnim_Do2:
		moveq	#0,d1
		move.b	$1B(a0),d1	; load current frame number
		move.b	1(a1,d1.w),d0	; read sprite number from script
		cmpi.b	#$F0,d0
		bcc.s	SAnim_End_FF	; if animation is complete,branch
; loc_10B10:
SAnim_Next:
		move.b	d0,$1A(a0)	; load sprite number
		addq.b	#1,$1B(a0)	; go to next frame
; return_10B18:
SAnim_Delay:
		rts
; ===========================================================================
; loc_10B1A:
SAnim_End_FF:
		addq.b	#1,d0		; is the end flag = $FF?
		bne.s	SAnim_End_FE	; if not,branch
		move.b	#0,$1B(a0)	; restart the animation
		move.b	1(a1),d0	; read sprite number
		bra.s	SAnim_Next
; ===========================================================================
; loc_10B2A:
SAnim_End_FE:
		addq.b	#1,d0		; is the end flag = $FE?
		bne.s	SAnim_End_FD	; if not,branch
		move.b	2(a1,d1.w),d0	; read the next byte in the script
		sub.b	d0,$1B(a0)	; jump back d0 bytes in the script
		sub.b	d0,d1
		move.b	1(a1,d1.w),d0	; read sprite number
		bra.s	SAnim_Next
; ===========================================================================
; loc_10B3E:
SAnim_End_FD:
		addq.b	#1,d0		; is the end flag = $FD?
		bne.s	SAnim_End	; if not,branch
		move.b	2(a1,d1.w),$1C(a0)	; read next byte,run that animation
; return_10B48:
SAnim_End:
		rts
; ===========================================================================
; loc_10B4A:
SAnim_WalkRun:
		subq.b	#1,$1E(a0)	; is the start flag = $FF?
		bpl.s	SAnim_Delay	; if not,branch
		addq.b	#1,d0		; is animation walking/running?
		bne.w	SAnim_Roll	; if not,branch
		moveq	#0,d0
		move.b	$27(a0),d0
		bne.w	SAnim_Tumble
		moveq	#0,d1
		move.b	$26(a0),d0	; get Sonic's angle
		move.b	$22(a0),d2
		andi.b	#1,d2		; is Sonic mirrored horizontally?
		bne.s	+		; if yes,branch
		not.b	d0		; reverse angle
+
		addi.b	#$10,d0		; add $10 to angle
		bpl.s	+		; if angle is 0-$7F,branch
		moveq	#3,d1
+
		andi.b	#$FC,1(a0)
		eor.b	d1,d2
		or.b	d2,1(a0)
		btst	#5,$22(a0)
		bne.w	SAnim_Push
		lsr.b	#4,d0		; divide angle by 16
		andi.b	#6,d0		; angle must be 0,2,4 or 6
		move.w	$14(a0),d2	; get Sonic's "speed" for animation purposes
		bpl.s	+
		neg.w	d2
+
		lea	(SonAni_Run).l,a1	; use running animation
		cmpi.w	#$600,d2		; is Sonic at running speed?
		bcc.s	+			; if yes,branch
		lea	(SonAni_Walk).l,a1	; use walking animation
+
		move.b	d0,d1
		lsr.b	#1,d1
		add.b	d1,d0
		add.b	d0,d0
		add.b	d0,d0
		move.b	d0,d3
		neg.w	d2
		addi.w	#$800,d2
		bpl.s	+
		moveq	#0,d2
+
		lsr.w	#8,d2
		lsr.w	#1,d2
		move.b	d2,$1E(a0)	; modify frame duration
		bsr.w	SAnim_Do2
		add.b	d3,$1A(a0)	; modify frame number
		rts
; ===========================================================================
; loc_10BD8:
SAnim_Tumble:
		move.b	$27(a0),d0
		moveq	#0,d1
		move.b	$22(a0),d2
		andi.b	#1,d2
		bne.s	SAnim_Tumble_Left

		andi.b	#$FC,1(a0)
		addi.b	#$B,d0
		divu.w	#$16,d0
		addi.b	#$9B,d0
		move.b	d0,$1A(a0)
		move.b	#0,$1E(a0)
		rts
; ===========================================================================
; loc_10C06:
SAnim_Tumble_Left:
		andi.b	#$FC,1(a0)
		tst.b	$29(a0)
		beq.s	loc_10C1E
		ori.b	#1,1(a0)
		addi.b	#$B,d0
		bra.s	loc_10C2A
; ===========================================================================

loc_10C1E:
		ori.b	#3,1(a0)
		neg.b	d0
		addi.b	#$8F,d0

loc_10C2A:
		divu.w	#$16,d0
		addi.b	#$9B,d0
		move.b	d0,$1A(a0)
		move.b	#0,$1E(a0)
		rts
; ===========================================================================
; loc_10C3E:
SAnim_Roll:
		addq.b	#1,d0		; is the start flag = $FE?
		bne.s	SAnim_Push	; if not,branch
		move.w	$14(a0),d2
		bpl.s	+
		neg.w	d2
+
		lea	(SonAni_Roll2).l,a1
		cmpi.w	#$600,d2
		bcc.s	+
		lea	(SonAni_Roll).l,a1
+
		neg.w	d2
		addi.w	#$400,d2
		bpl.s	+
		moveq	#0,d2
+
		lsr.w	#8,d2
		move.b	d2,$1E(a0)
		move.b	$22(a0),d1
		andi.b	#1,d1
		andi.b	#$FC,1(a0)
		or.b	d1,1(a0)
		bra.w	SAnim_Do2
; ===========================================================================
; loc_10C82:
SAnim_Push:
		move.w	$14(a0),d2
		bmi.s	+
		neg.w	d2
+
		addi.w	#$800,d2
		bpl.s	+
		moveq	#0,d2
+
		lsr.w	#6,d2
		move.b	d2,$1E(a0)
		lea	(SonAni_Push).l,a1
		move.b	$22(a0),d1
		andi.b	#1,d1
		andi.b	#$FC,1(a0)
		or.b	d1,1(a0)
		bra.w	SAnim_Do2
