		move.b	#$F,$16(a0)	; sets standing height to only slightly higher than rolling height,unlike Sonic
		move.b	#9,$17(a0)
		move.b	#5,$1C(a0)
		subq.w	#5,$C(a0)
; loc_11408:
Tails_SetRollSpeed:
		move.b	$26(a0),d0
		jsr	(CalcSine).l
		muls.w	$14(a0),d0
		asr.l	#8,d0
		move.w	d0,$12(a0)	; set y velocity based on $14 and angle
		muls.w	$14(a0),d1
		asr.l	#8,d1
		cmpi.w	#$1000,d1
		ble.s	loc_1142C
		move.w	#$1000,d1	; limit Tails' speed rolling right

loc_1142C:
