; loc_23BBC:
.islevel:
		moveq	#0,d0
		move.b	(Current_Zone).w,d0
; loc_23BC2:
.selectlist:
		lea	(JmpTbl_DbgObjLists).l,a2
		add.w	d0,d0
		adda.w	(a2,d0.w),a2
		move.w	(a2)+,d6
		cmp.b	(Debug_object).w,d6
		bhi.s	+
		move.b	#0,(Debug_object).w
+
