		btst	#1,d4
		beq.s	.moveLeft
		add.l	d1,d2
		moveq	#0,d0
		move.w	(Camera_Max_Y_pos).w,d0
		addi.w	#$DF,d0
		swap	d0
		cmp.l	d0,d2
		blt.s	.moveLeft
		move.l	d0,d2
